mapper.select:
select u.user_id, u.phone, u.avatar, u.nickname, u.create_time, u.update_time, u.state, u.sex, (select username from sys_admin where user_id = u.user_id ) as username, group_concat(distinct r.role_name separator ',') as roleName from sys_user u join sys_role_user ru on u.user_id = ru.user_id join sys_role r on ru.role_id = r.role_id where r.role_value >= 100 and u.deleted = 0
and u.phone like concat('%',#{sysUser.phone},'%')
and u.sex = #{sysUser.sex}
and u.state = #{sysUser.state}
and u.nickname like concat('%',#{sysUser.nickname},'%')
and ru.role_id = #{roleId}
and u.user_id in ( select ac.user_id from sys_admin ac where ac.username like concat('%',#{username},'%'))
group by u.user_id order by u.update_time desc limit #{page},#{size}

select count( u.user_id) from sys_user u join sys_role_user ru on u.user_id = ru.user_id join sys_role r on ru.role_id = r.role_id where r.role_value >= 100 and u.deleted = 0
and u.phone like concat('%',#{sysUser.phone},'%')
and u.sex = #{sysUser.sex}
and u.state = #{sysUser.state}
and u.nickname like concat('%',#{sysUser.nickname},'%')
and ru.role_id = #{roleId}
and u.user_id in ( select user_id from sys_admin where username like concat('%',#{username},'%'))
